import axios from "axios";
import { useEffect, useState } from "react";
import { useNavigate, useParams } from "react-router-dom";

function Edit(){
    const parasm2=useParams()
    console.log(parasm2.ID)
    let nav=useNavigate()
    const [data,setData]=useState("")
    const [title,setitle]=useState("")
    const [price,setPirce]=useState("")
    useEffect(()=>{
        fetch(`http://localhost:9000/prodact/${parasm2.ID}`)
        .then(res=>res.json())
        .then(data=>setData(data))
    },[])
function Form1(e){
    e.preventDefault()
    axios.put(`http://localhost:9000/prodact/${parasm2.ID}`,{
        price,title
    }).then()
    nav("/prodact")



  
}
return(
<div>
<form onSubmit={Form1} >
<div className="mb-3">
<label htmlFor="title" className="form-label">Title address</label>
<input  onChange={(e)=>setitle(e.target.value)}   type="text" className="form-control" id="title"placeholder={data.title}/>
</div>
<div className="mb-3">
<label htmlFor="prodact price" className="form-label">price</label>
<input  onChange={(e)=>setPirce(e.target.value)}   type="text" className="form-control"   id="prodact price" placeholder={data.price}  />
</div>
<button  type="submit" className="btn btn-primary">Add Prodact</button>
</form>    
        </div>
    )
}
export default Edit
