import axios from "axios";
import { useEffect, useState } from "react";
import { Form, useParams } from "react-router-dom";

export default function Edit() {
  const [data, setdata] = useState("");
  const [title, setitle] = useState("");
  const [price, setprice] = useState(0);
  let params = useParams();
let navagete = useNavigate();

  useEffect(() => {
    fetch(`http://localhost:8000/Prodact/${params.id}`)
      .then((res) => res.json())
      .then((data) => setdata(data));
  }, []);
  function Form(e) {
    e.preventDefault();
    axios.post(`http://localhost:8000/Prodact/${params.id}`, {
      title,
      price
    })
      .then(
        
        navagete("/Prodact")
      );

  }
  return (
    <div>
      <form onSubmit={Form}>
        <div className="mb-3 ">
          <h2 htmlFor="title pt-3" className="form-label">
            Edit Some Prodat
          </h2>
          <input
            value={data.title}
            type="text"
            className="form-control"
            id="title"
            placeholder="title"
          />
        </div>
        <div className="mb-3">
          <label htmlFor="prodact price" className="form-label">
            price
          </label>
          <input
            value={data.price}
            type="text"
            className="form-control"
            id="prodact price"
            placeholder="Price"
          />
        </div>
        <button type="submit" className="btn btn-primary">
          Edit Prodact
        </button>
      </form>
    </div>
  );
}
