            .then(json=>setcategories(json))
    },[])
    const Care= (NameCat)=>{
     
        fetch(`https://fakestoreapi.com/products/category/${NameCat}`)
        .then(res=>res.json())
        .then(json=>setstate(json))
   
    
    }
    return (
        <div>
 <h2 className="text-center mt-4">Our Prodact</h2>
 <div className="container">
    <div className="d-flex justify-content-center">
 {categories.map((cat)=>{
    return(
        <button onClick={()=> {Care(cat)}} className="btn btn-info mt-4 mb-4" key={cat}>{cat}</button>
     
    )
 })}
 <button onclick className="btn btn-info mt-4 mb-4">ALL</button>
 </div>

