import axios from "axios"
import { useState } from "react"
import { useNavigate, useParams } from "react-router-dom"
function Edit(){
    const[title,settitle]=useState("")
    const[price,setprice]=useState(0)
    let params=useParams()
// let nav=useNavigate()
function FormSubmit(e){


e.preventDefault()
axios.put(`http://localhost:9000/prodact/${params}`,{
    title,
    price
})
// nav("../Prodacts")
}
return(
<div>
<h1>Updata the prodact</h1>
<form onSubmit={FormSubmit} >
<div className="mb-3">
<label htmlFor="exampleInputEmail1" className="form-label">Title </label>
<input onChange={(e)=>settitle(e.target.value)}  type="text" placeholder="Prodact Title" className="form-control" id="exampleInputEmail1" aria-describedby="prodact Title" />
</div> 
<div className="mb-3">
<label htmlFor="exampleInputEmail1" className="form-label">Price </label>
<input onChange={(e)=>setprice(e.target.value)}  type="text" placeholder="Prodact Price" className="form-control" id="exampleInputEmail1" aria-describedby="prodact Title" />
</div> 
<button type="submit" className="btn btn-primary">Edit</button>
</form>
</div>
)}
export default Edit
