import axios from "axios"
import { useState } from "react"
import { useEffect } from "react"
import { Link } from "react-router-dom"
import Swal from 'sweetalert2'
function Prodacts(){
const [data,setdata]=useState([])
useEffect(()=>{
fetch(" http://localhost:9000/prodact")
.then(res=>res.json())
.then(data=>setdata(data))
},[])
const Delete=(Prp)=>{
Swal.fire({
title:`Are You Sure To Delete?${Prp}`,
showCancelButton:true
}).then((data=>{
if(data.isConfirmed){
axios(`http://localhost:9000/prodact/${Prp}`,{
method:"DELETE"
})
fetch("http://localhost:9000/prodact")
.then(res=>res.json())
.then(data=>setdata(data))
}
}))
}
return(
<div className="p-3">
<h1>Prodacts</h1>
<Link to={"/Prodacts/Add"} className="btn btn-success">Add New Prodact</Link>
<table className="table table-striped">
<thead>
<tr>
<th>ID</th>
<th>Title</th>
<th>Price</th>
<th>Operiton</th>
</tr>
</thead>
<tbody>
{data.map((prodact)=>{

return( 
<tr>
<td >{prodact.id}</td>
<td >{prodact.title.slice(0,50)}...</td>
<td>{prodact.price }$</td>
<td>
<button className="btn btn-danger me-1 btn-sm" onClick={()=>{Delete(prodact.id)}}>Delete</button>
<Link to={`/Prodacts/${prodact.id}`} className="btn btn-info me-1 btn-sm">View</Link>
<Link onClick={()=>Edit(prodact.id)} className="btn btn-primary btn-sm">Edit</Link>
</td>
</tr>

)

})}

</tbody>
</table>
</div>
)
}
export default Prodacts
