import React from "react"
import "./Style.css"
import axios from "axios"
import Header from "./Header"

function Login(){
const [Email,setEmail]=React.useState("")
const [password,setpassword]=React.useState("")
const [Accept,setAccept]=React.useState(false)
const [Erorr,setErorr]=React.useState("")

async function Submite(e){
    let Flag=true
    e.preventDefault()
    setAccept(true)
    if(password.length<8){
        Flag=false
    }else{
        Flag=true
    }
    try{
    if(Flag){
    let res=await axios.post('http://127.0.0.1:8000/api/login',{
        email:Email,
        password:password
     })
     if(res.status===200){
        window.location.pathname='/'
     }
 

    }
}catch(eror){
    setErorr(eror.response.status )

}
}

return(
    <div>
        <Header/>
    
<div className="Father">
   
<form onSubmit={Submite}>

<label htmlFor="in2">Email</label>
<input id="in2" type="email" placeholder="Email..." value={Email}  onChange={(e)=>setEmail(e.target.value)}  />
{ Erorr===422&&Accept&&<p className="Eroor"> The Email is Used</p>}
<label htmlFor="in3">Password</label>
<input id="in3" type="password" placeholder="password..."value={password}  onChange={(e)=>setpassword(e.target.value)}  />
{password.length<8&&Accept&&<p className="Eroor">The password is mpre the 8</p>}
<label htmlFor="in4">Rebet Password</label>


<div style={{textAlign:"center"}}>
<button className="btn">Login</button>
</div>
</form>
</div>
</div>
    )
}
export default Login
