import { useEffect, useState } from "react"
import ProdactrList from "./ProdactList"
function Prodact(){
    function ClickHand(cat){
        console.log(cat)
    }
    const [Prod,setprod]=useState([])
    const [category,Setcategory]=useState([])
        useEffect(()=>{
            fetch("https://fakestoreapi.com/products")
            .then(res=>res.json())
            .then(data=>setprod(data))
        },[])
        useEffect(()=>{
            fetch('https://fakestoreapi.com/products/categories')
            .then(res=>res.json())
            .then(json=>Setcategory(json))
        })
  const category1 =useEffect((cat)=>{
            fetch(`https://fakestoreapi.com/products/category/${cat}`)
            .then(res=>res.json())
            .then(json=>console.log(json))
        })
return(
<div>
<h2 className="text-center mt-5">Our Prodacts</h2>
<div className="container  text-center">
    {category.map((cat)=>{
        return(
         <button onClick={()=>category1} className="btn btn-primary me-2 mb-4 mt-4" key={cat}>{cat}</button>
            
        )
        
    })
}

   
<div className="row">
{Prod.map((pro)=>{
return(
<ProdactrList Prodact1={pro} key={pro.id} />
)
})}
</div>

</div>

      
    </div>

    )
}
export default Prodact
