import React from 'react';
import './all.min.css'

function App(){
const [bol,setbol]=React.useState(true)
function Toggle(){
    setbol(bol?false:true)
}

    return(
        <i onClick={Toggle} style={{textAlign:"center",font ,color:bol?"red":"Yellow"}} class="fa-solid fa-star"></i>
    )

}
export default App
