import {  createAsyncThunk, createSlice } from '@reduxjs/toolkit'
export const Asunc=createAsyncThunk('ProdactReduser1/Asunc',async()=>{
  const res=await fetch('https://fakestoreapi.com/products')
  const data=await res.json()
  console.log(data);
  return data
})
export const ProdactReduser1=createSlice({
initialState:[{id:1,title:"Asas"}],
name:"ProdactReduser1",
reducers:{
addprodact:(state,action)=>{
  state.push(action.payload)   
}
},
extraReducers:(builder)=>{
  builder.addCase(Asunc.fulfilled, (state, action) =>{
    console.log(action);
    state=action.payload
  } )
}
})
export const {addprodact}=ProdactReduser1.actions
export default ProdactReduser1.reducer
