import { useState } from "react"
import { useEffect } from "react"
import { Link } from "react-router-dom"

function Prodact(){
    const [Data,setData]=useState([])
    useEffect(()=>{
        Efect()
    },[]  )
    const Efect=()=>{
        fetch("http://localhost:9000/prodact")
        .then(res=>res.json())
        .then(data=>setData(data))
    }
    const Delete=(prodact)=>{
        fetch(`http://localhost:9000/prodact/${prodact}`,{
            method:"DELETE"
        }).then(res=>res.json())
        .then(Efect())

    }
return(
<div className="container  ">
<h2 className="mb-5 pt-4">prodact Page</h2>
<Link to={"/Prodact/Add"} type="button" className="btn btn-success mb-4">Add Prodact</Link>
<table className="table table-striped">
<thead>
<tr>
    <th>ID</th>
    <th>Title</th>
    <th>Price</th>
    <th>Operation</th>
    </tr>
</thead>
<tbody>
    {Data.map((prodact)=>{
            return(
            <tr key={prodact.id}>
            <td>{prodact.id}</td>
            <td>{prodact.title.slice(0,100)}...</td>
    <td>{prodact.price}</td> 
   
    <td>   
    <button onClick={()=>Delete(prodact.id)} type="button" className="btn btn-danger  btn-sm">Delte</button>
   <Link to={`/prodact/${prodact.id}`} className="btn btn-info btn-sm">View</Link>
   <button className="btn btn-primary btn-sm">Edit</button>
   </td>

            </tr>
            
        )
    })}

</tbody>
</table>
</div>
)
}
export default Prodact

