import axios from "axios"
import { useEffect, useState } from "react"
import { useNavigate, useParams } from "react-router-dom"
function Edit(){
const[title,settitle]=useState("")
const[data,setdata]=useState("")
const[price,setprice]=useState(0)
let usenav=useNavigate()
let {Id}=useParams()
useEffect(()=>{
fetch(`http://localhost:9000/prodact/${Id}`)
.then(res=>res.json())
.then(data=>setdata(data))
})
function FormSubmit(e){
e.preventDefault()
axios.put(`http://localhost:9000/prodact/${Id}`,{
title,
price
})
.then((data)=>{
    usenav("/Prodacts")
  })

}
return(
<div>
<h1>Updata the prodact</h1>
<form onSubmit={FormSubmit} >
<div className="mb-3">
<label htmlFor="exampleInputEmail1" className="form-label">Title </label>
<input value={data.title} onChange={(e)=>settitle(e.target.value)}  type="text" placeholder="Prodact Title" className="form-control" id="exampleInputEmail1" aria-describedby="prodact Title" />
</div> 
<div className="mb-3">
<label htmlFor="exampleInputEmail1" className="form-label">Price </label>
<input  value={data.price} onChange={(e)=>setprice(e.target.value)}  type="text" placeholder="Prodact Price" className="form-control" id="exampleInputEmail1" aria-describedby="prodact Title" />
</div> 
<button type="submit" className="btn btn-primary">Edit</button>
</form>
</div>
)}
export default Edit
